import React from 'react';
import { Link as ScrollLink, Link } from 'react-scroll';
import { useHistory } from 'react-router-dom'; // Import useHistory
import './Header.css';

const Header = () => {
  const history = useHistory(); // Initialize useHistory

  const handleStoreClick = () => {
    history.push('/'); // Navigate to the root URL (product list page)
  };

  return (
    <header className="header">
      <nav className="navbar">
        <div className="navbar-item">
          {/* Use Link to scroll and Link to navigate */}
          <Link to="product-list" smooth={true} duration={500} className="navbar-item">
            <li className="store" onClick={handleStoreClick}>E-commerce Store</li>
          </Link>
          <ScrollLink
            to="product-list"
            smooth={true}
            duration={500}
            className="navbar-item"
          >
            <li>Products</li>
          </ScrollLink>
        </div>
      </nav>
    </header>
  );
};

export default Header;
