import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProducts } from '../../utils/Store'; // Adjust the import path
import { ErrorContext } from '../../context/ErrorContext'; // Adjust the import path

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { error, setError } = useContext(ErrorContext); // Use useContext to access the error state

  useEffect(() => {
    fetchProducts()
      .then(data => {
        const selectedProduct = data.find(item => item.id === parseInt(id));
        setProduct(selectedProduct);
        setError(null);
      })
      .catch(err => {
        console.error('Error fetching data:', err);
        setError('An error occurred while fetching data. Please try again.');
      });
  }, [id, setError]);

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="product-details">
      {error && <div className="error-message">{error}</div>}
      <img src={product.image} alt={product.title} />
      <h2>{product.title}</h2>
      <p className="price">${product.price}</p>
      <p className="rating">Rating: {product.rating.rate}</p>
      <p>{product.description}</p>
    </div>
  );
};

export default ProductDetails;
