import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProducts } from '../../utils/Store';
import { useErrorContext } from '../../context/ErrorContext'; // Adjust the import path

const ProductDetails = () => {
  const { id } = useParams();
  const { error, setError } = useErrorContext(); // Utilize the hook here
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetchProduct(id)
      .then(data => {
        setProduct(data);
        setError(null);
      })
      .catch(error => {
        console.error('Error fetching product:', error);
        setError('An error occurred while fetching the product. Please try again.');
        setProduct(null);
      });
  }, [id, setError]);

  return (
    <div className="product-details">
      {error && <div className="error-message">{error}</div>}
      {product ? (
        <div>
          {/* ... display product details */}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductDetails;
